import React, { useState, useCallback, useMemo } from 'react';
import { createEditor, Editor } from 'slate';
import { Slate, Editable, withReact } from 'slate-react';
import { withHistory } from 'slate-history';
import ChatPanel from './ChatPanel';
import InlineControls from './InlineControls';
import Toolbar from './Toolbar';
import { useSOWStore } from '../stores/sowStore';

const SOWEditor = () => {
  const {
    sections,
    documentTitle,
    inlineControls,
    showInlineControls,
    hideInlineControls
  } = useSOWStore();

  const editor = useMemo(() => withHistory(withReact(createEditor())), []);

  const [value, setValue] = useState([
    {
      type: 'paragraph',
      children: [{ text: 'Start writing your Statement of Work...' }],
    },
  ]);

  const renderElement = useCallback((props) => <Element {...props} />, []);
  const renderLeaf = useCallback((props) => <Leaf {...props} />, []);

  const handleChange = useCallback((newValue) => {
    setValue(newValue);
    // Document updates will be handled when we implement section management
  }, []);

  const handleSelectionChange = useCallback(() => {
    const { selection } = editor;

    if (selection && !Editor.isCollapsed(editor, selection)) {
      const selectedText = Editor.string(editor, selection);
      if (selectedText.trim()) {
        // Show inline controls for selected text
        const domSelection = window.getSelection();
        if (domSelection.rangeCount > 0) {
          const domRange = domSelection.getRangeAt(0);
          const rect = domRange.getBoundingClientRect();

          showInlineControls({
            x: rect.left + rect.width / 2,
            y: rect.top - 10
          });
        }
      }
    } else {
      hideInlineControls();
    }
  }, [editor, showInlineControls, hideInlineControls]);

  return (
    <div className="editor-container">
      <div className="editor-panel">
        <Slate 
          editor={editor} 
          initialValue={value} 
          onChange={handleChange}
        >
          <Toolbar />
          <div className="slate-editor">
            <Editable
              renderElement={renderElement}
              renderLeaf={renderLeaf}
              placeholder="Start writing your Statement of Work..."
              onSelectionChange={handleSelectionChange}
              spellCheck
              autoFocus
            />
          </div>
        </Slate>
        {inlineControls.visible && <InlineControls />}
      </div>
      <ChatPanel />
    </div>
  );
};

// Element renderer for different node types
const Element = ({ attributes, children, element }) => {
  const style = { textAlign: element.align };

  switch (element.type) {
    case 'block-quote':
      return (
        <blockquote style={style} {...attributes}>
          {children}
        </blockquote>
      );
    case 'bulleted-list':
      return (
        <ul style={style} {...attributes}>
          {children}
        </ul>
      );
    case 'heading-one':
      return (
        <h1 style={style} {...attributes}>
          {children}
        </h1>
      );
    case 'heading-two':
      return (
        <h2 style={style} {...attributes}>
          {children}
        </h2>
      );
    case 'list-item':
      return (
        <li style={style} {...attributes}>
          {children}
        </li>
      );
    case 'numbered-list':
      return (
        <ol style={style} {...attributes}>
          {children}
        </ol>
      );
    default:
      return (
        <p style={style} {...attributes}>
          {children}
        </p>
      );
  }
};

// Leaf renderer for text formatting
const Leaf = ({ attributes, children, leaf }) => {
  if (leaf.bold) {
    children = <strong>{children}</strong>;
  }

  if (leaf.code) {
    children = <code>{children}</code>;
  }

  if (leaf.italic) {
    children = <em>{children}</em>;
  }

  if (leaf.underline) {
    children = <u>{children}</u>;
  }

  return <span {...attributes}>{children}</span>;
};

export default SOWEditor;
